class Book{constructor(e,t,o){this.title=e,this.author=t,this.isbn=o}}class UI{addBookToList(e){const t=document.getElementById("book-list"),o=document.createElement("tr");o.innerHTML=`\n        \n        <td>${e.title}</td>\n        <td>${e.author}</td>\n        <td>${e.isbn}</td>\n        <td><a href="" class="delete">X</a></td>\n        `,t.appendChild(o)}showAlert(e,t){const o=document.createElement("div");o.className=`alert ${t}`,o.appendChild(document.createTextNode(e));const n=document.querySelector(".container"),l=document.querySelector("#book-form");n.insertBefore(o,l),setTimeout(function(){document.querySelector(".alert").remove()},3e3)}deleteBook(e){"delete"===e.className&&e.parentElement.parentElement.remove()}clearFields(){document.getElementById("title").value="",document.getElementById("author").value="",document.getElementById("isbn").value=""}}document.getElementById("book-form").addEventListener("submit",function(e){const t=document.getElementById("title").value,o=document.getElementById("author").value,n=document.getElementById("isbn").value,l=new Book(t,o,n),d=new UI;""===t||""===o||""===n?d.showAlert("please fill in all fields","error"):(d.addBookToList(l),d.showAlert("Book Added","success"),d.clearFields()),e.preventDefault()}),document.getElementById("book-list").addEventListener("click",function(e){const t=new UI;t.deleteBook(e.target),t.showAlert("Book Removed!","success"),e.preventDefault()});